MERGE INTO MONTHLY_STATISTICS
USING (SELECT S.CITYPLACED AS City, MIN(M.MEASVALUE) AS Minim, MAX(M.MEASVALUE) AS Maxim, ROUND(AVG(M.MEASVALUE),1) AS Avrg, S.SENSORTYPE AS S_Type, TO_CHAR(M.DATETIME, 'MM/YY') AS CurDate
FROM MEASUREMENT M INNER JOIN SENSOR S ON M.SENSORID = S.SERIALNUMBER
WHERE TO_CHAR(M.DATETIME, 'MM/YY') = '05/18'
GROUP BY S.CITYPLACED, S.SENSORTYPE, TO_CHAR(M.DATETIME, 'MM/YY')) TMP
ON (TMP.City = M_STATCITY AND TMP.CurDate = TO_CHAR(MONTHID, 'MM/YY') AND TMP.S_Type = M_STATTYPE)
WHEN NOT MATCHED THEN INSERT (MONTHID, M_STATCITY, M_STATMIN, M_STATMAX, M_STATAVG, M_STATTYPE) VALUES (TO_DATE(TMP.CurDate, 'MM/YY'), TMP.City, TMP.Minim, TMP.Maxim, TMP.Avrg, TMP.S_Type)
WHEN MATCHED THEN UPDATE SET M_STATMIN = TMP.Minim, M_STATMAX = TMP.Maxim, M_STATAVG = TMP.Avrg;

